import * as React from "react";

import "styles/animate.scss";
import { TransactionState } from "providers/transactions";
import { useSelectTransaction } from "providers/transactions/selected";
import { useSlotTiming } from "providers/slot";
import type { SlotTiming } from "providers/slot";

interface Props {
  transaction: TransactionState;
}

function timeElapsed(
  sentAt: number | undefined,
  receivedAt: number = performance.now()
): number | undefined {
  if (sentAt === undefined) return;
  return parseFloat(((receivedAt - sentAt) / 1000).toFixed(3));
}

export function TxTableRow({ transaction }: Props) {
  const signature = transaction.details.signature;
  const selectTransaction = useSelectTransaction();
  const slotMetrics = useSlotTiming();

  let targetSlot;
  let landedSlot;
  if (transaction.status === "success") {
    targetSlot = transaction.slot.target;
    landedSlot = transaction.slot.landed;
  } else if (transaction.status === "timeout") {
  } else {
    targetSlot = transaction.pending.targetSlot;
  }

  let slotTiming: SlotTiming | undefined;
  if (landedSlot !== undefined) {
    slotTiming = slotMetrics.current.get(landedSlot);
  }

  return (
    <tr className="debug-row" onClick={() => selectTransaction(signature)}>
      <td className="text-monospace">{signature.slice(0, 10)}...</td>
      <td>{targetSlot || "-"}</td>
      <td>{landedSlot || "-"}</td>
      <td>{slotTiming?.avgTpe?.toFixed(1) || "-"}</td>
      <td>{slotTiming?.maxTpe || "-"}</td>
      <td>{slotTiming?.firstShred || "-"}</td>
      <td>{timeElapsed(slotTiming?.firstShred, slotTiming?.fullSlot)?.toFixed(3) || "-"}</td>
      <td>{timeElapsed(slotTiming?.firstShred, slotTiming?.replayStart)?.toFixed(3) || "-"}</td>
      <td>{timeElapsed(slotTiming?.firstShred, slotTiming?.frozen)?.toFixed(3) || "-"}</td>
      <td>{timeElapsed(slotTiming?.firstShred, slotTiming?.voted)?.toFixed(3) || "-"}</td>
      <td>{timeElapsed(slotTiming?.firstShred, slotTiming?.confirmed)?.toFixed(3) || "-"}</td>
      <td>{timeElapsed(slotTiming?.firstShred, slotTiming?.rooted)?.toFixed(3) || "-"}</td>
      <td>{timing?.confirmed?.toFixed(3) || "-"}</td>
    </tr>
  );
}
